mapper.select:
select

from appfile
where 1=1

  and name = #{name}


  and mtype = #{mtype}

order by latestflag desc, appfileid desc

select

from appfile
where name = #{name}
and mtype = #{mtype}
and vcode = #{vcode}

select

from appfile
where name = #{name}
and mtype = #{mtype}
and latestflag = 1

mapper.delete:
update appfile
set latestflag=0
where name = #{name}
and mtype = #{mtype}

update appfile
set latestflag=1
where name = #{name}
and mtype = #{mtype}
order by vcode desc
limit 1
