mapper.select:
select 

from attendancescheme
where enableflag='1' and orgid=#{orgid}

select 

from attendancescheme
where attendanceschemeid = #{attendanceschemeid,jdbcType=INTEGER} and orgid=#{orgid}

mapper.update:

