mapper.select:
select count(1) from bundle
where status = 1 
and orgid = #{orgid}
and branchid = #{branchid}

  and reviewflag = #{reviewflag}


  and touchflag = #{touchflag}


  and homeflag = #{homeflag}


  and name like #{search}

select 
from bundle
where status=1
and orgid = #{orgid}
and branchid = #{branchid}

  and reviewflag = #{reviewflag}


  and touchflag = #{touchflag}


  and homeflag = #{homeflag}


  and name like #{search}

order by bundleid desc 

  limit ${start}, ${length}

mapper.delete:
update bundle 
set status = '9'
where bundleid = #{bundleid}
