mapper.select:
select count(1) from bundle
where status = 1 and orgid=${orgid}

  and name like #{search}

select * from bundle
where status=1

  and orgid = ${orgid}


  and name like #{search}

order by bundleid desc 

  limit ${start}, ${length}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into bundle (orgid, layoutid, name, 
  status, createtime, createstaffid
  )
values (#{orgid,jdbcType=INTEGER}, #{layoutid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{status,jdbcType=CHAR}, #{createtime,jdbcType=TIMESTAMP}, #{createstaffid,jdbcType=INTEGER}
  )
