mapper.select:
select 

from classroom
where classroomid=#{classroomid} and orgid=#{orgid}

select count(1)  from  classroom 
where orgid=#{orgid}  and name=#{name}

   and  classroomid != #{excludeid}

mapper.delete:
delete from  classroom 
where orgid=#{orgid} and classroomid in 

  #{classroomid}
