mapper.select:
select * from dailyplaylog
where deviceid=#{deviceid}
and mediatype = #{mediatype}
and mediaid = #{mediaid}
and date_format(playdate,'%Y%m%d%H') = #{day}

select deviceid, mediatype, mediaid, sum(total) as amount
from dailyplaylog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d') >= #{from}
and date_format(starttime,'%Y%m%d') <= #{to}
group by mediatype, mediaid
order by amount desc

select deviceid, date_format(starttime,'%Y%m%d%H') as period, mediatype, mediaid, sum(total) as amount
from dailyplaylog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d%H') = #{hour}
group by mediatype, mediaid
order by amount desc

select deviceid, date_format(starttime,'%Y%m%d') as period, mediatype, mediaid, sum(total) as amount
from dailyplaylog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d') = #{day}
group by mediatype, mediaid
order by amount desc

select deviceid, date_format(starttime,'%Y%m') as period, mediatype, mediaid, sum(total) as amount
from dailyplaylog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m') = #{month}
group by mediatype, mediaid
order by amount desc
