mapper.select:
select count(1) from device 
where 1 = 1

  and orgid = #{orgid}


  and branchid = #{branchid}


  and status = #{status}


  and devicegroupid = #{devicegroupid}


  and devicegridid = #{devicegridid}


  and (terminalid like #{search} or name like #{search})

select * from device
where 1 = 1

  and orgid = #{orgid}


  and branchid = #{branchid}


  and status = #{status}


  and onlineflag = #{onlineflag}


  and devicegroupid = #{devicegroupid}


  and devicegridid = #{devicegridid}


  and (terminalid like #{search} or name like #{search})


  order by deviceid


  order by refreshtime desc


  limit ${start}, ${length}

select * from device
where devicegroupid = #{devicegroupid}
order by deviceid

select * from device
where devicegridid = #{devicegridid}
order by ypos, xpos

select * from device
where status = 1 
and hardkey = #{hardkey}

select * from device
where terminalid = #{terminalid}
order by deviceid

