mapper.select:
select count(1) from devicegroup 
where status = 1
and type = #{type}

  and orgid = #{orgid}


  and branchid = #{branchid}


  and gridlayoutcode = #{gridlayoutcode}


  and name like #{search}

select * from devicegroup
where status=1
and type = #{type}

  and orgid = #{orgid}


  and branchid = #{branchid}


  and gridlayoutcode = #{gridlayoutcode}


  and name like #{search}

order by devicegroupid desc 

  limit ${start}, ${length}

mapper.update:
update device
set devicegroupid = 0 
where devicegroupid = #{devicegroupid}

update devicegrid
set devicegroupid = 0 
where devicegroupid = #{devicegroupid}
