mapper.select:
select count(1) from diy
where status = 1 
and orgid = #{orgid}

  and branchid = #{branchid}


  and name like #{search}

select * from diy
where status = 1 
and orgid = #{orgid}

  and branchid = #{branchid}


  and name like #{search}

order by diyid desc 

  limit ${start}, ${length}

mapper.delete:
update diy
set status='9'
where diyid = #{diyid}
