mapper.select:
select count(1) from dvb
where type = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and status = #{status}


  and name like #{search}

select * from dvb
where type = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and status = #{status}


  and name like #{search}

order by dvbid desc 

  limit ${start}, ${length}

mapper.delete:
update dvb
set status='9'
where dvbid = #{dvbid}
