mapper.select:
select 

from examinationroom
where examinationroomid = #{examinationroomid,jdbcType=INTEGER}  and orgid=#{orgid}

select ex.*, IFNULL(cr.name,'') AS classroomname from examinationroom ex left join classroom cr on ex.classroomid = cr.classroomid where ex.orgid = #{orgid}
and (ex.name like #{search} or ex.description like #{search} or cr.name like #{search})
order by ex.examinationroomid desc
