mapper.select:
select
from gridschedule where devicegridid = #{devicegridid}
and playmode = #{playmode}
and playdate >= '${fromdate}'
and playdate <= '${todate}'
order by playdate, starttime

mapper.delete:
delete from gridschedule
where devicegridid = #{devicegridid} 

  and playmode = #{playmode}


  and playdate = #{playdate}


  and starttime = #{starttime}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into gridschedule (devicegridid, playmode, playdate, 
  starttime, endtime, createtime
  )
values (#{devicegridid,jdbcType=INTEGER}, #{playmode,jdbcType=CHAR}, #{playdate,jdbcType=DATE}, 
  #{starttime,jdbcType=TIME}, #{endtime,jdbcType=TIME}, #{createtime,jdbcType=TIMESTAMP}
  )
