mapper.select:
select * from hourflowlog
where deviceid=#{deviceid}
and flowhour = #{flowhour}

select total from hourflowlog
where deviceid = #{deviceid}
and flowhour = date_format(now(),'%Y%m%d%H')

select total from hourflowlog
where deviceid = #{deviceid}
and flowhour = date_format(date_sub(now(), interval 1 hour),'%Y%m%d%H')

select sum(total) from hourflowlog
where deviceid = #{deviceid}
and flowdate = date_format(now(),'%Y%m%d')

select sum(total) from hourflowlog
where deviceid = #{deviceid}
and flowdate = date_format(date_sub(now(), interval 1 day),'%Y%m%d')

select count(1) from device
where status = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and (terminalid like #{search} or name like #{search})

select deviceid, terminalid, name, onlineflag from device
where status = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and (terminalid like #{search} or name like #{search})

order by deviceid

  limit ${start}, ${length}

select right(flowhour,2) as sequence, flowhour as period, sum(total) as amount
from hourflowlog
where deviceid = #{deviceid}
and flowdate = #{day}
group by period
order by period

select right(flowdate,2) as sequence, flowdate as period, sum(total) as amount
from hourflowlog
where deviceid = #{deviceid}
and left(flowdate,6) = #{month}
group by period
order by period
