mapper.select:
select count(1) from image where status = 1

  and orgid = #{orgid}


  and branchid = #{branchid}


  and folderid=#{folderid}


  and adflag = #{adflag}


  and name like #{search}

select  from image
where status=1 

  and orgid = #{orgid}


  and branchid = #{branchid}


  and folderid=#{folderid}


  and adflag = #{adflag}


  and name like #{search}

order by imageid desc 

  limit ${start}, ${length}

mapper.delete:
update image
set status='9'
where imageid = #{imageid}
