mapper.select:
select count(1) from image where status = 1

  and orgid = #{orgid}


  and branchid = #{branchid}


  and objtype = #{objtype}


  and objid = #{objid}


  and name like #{search}

select  from image
where status=1 

  and orgid = #{orgid}


  and branchid = #{branchid}


  and objtype = #{objtype}


  and objid = #{objid}


  and name like #{search}

order by imageid desc 

  limit ${start}, ${length}
