mapper.select:
select count(1) from mediagrid 
where status != '9' 
and orgid = #{orgid}
and branchid = #{branchid}

  and status = #{status}


  and gridlayoutcode = #{gridlayoutcode}


  and name like #{search}

select * from mediagrid
where status != '9' 
and orgid = #{orgid}
and branchid = #{branchid}

  and status = #{status}


  and gridlayoutcode = #{gridlayoutcode}


  and name like #{search}

order by mediagridid desc 

  limit ${start}, ${length}
