mapper.select:
select count(1) from medialist 
where type = '1' 
and status = '1' 
and orgid = #{orgid}
and branchid = #{branchid}

  and name like #{search}

select * from medialist
where type = '1' 
and status = '1' 
and orgid = #{orgid}
and branchid = #{branchid}

  and name like #{search}

order by medialistid desc 

  limit ${start}, ${length}
