mapper.select:
SELECT m.*, st.name as bookstaffname, br.name as bookbranchname, mr.name AS meetingroomname, l.name AS locationname FROM meeting m LEFT JOIN meetingroom mr ON (m.status = '1' AND m.meetingroomid = mr.meetingroomid) LEFT JOIN staff st ON (m.bookstaffid = st.staffid) LEFT JOIN branch br ON (m.bookbranchid = br.branchid) LEFT JOIN location l ON (mr.locationid = l.locationid) WHERE mr.orgid = #{orgid} and mr.status='1' and (periodmeetingid>0 or periodflag='0')
and (m.subject like #{search} )
and m.meetingroomid=#{meetingroomid}
and m.starttime>=#{starttime}
and m.endtime <= #{endtime}
and m.auditstatus in
#{listItem}
and m.bookbranchid IN ( select tmp.branchid from branch tmp where tmp.branchid=#{bookbranchid} or tmp.parentid=#{bookbranchid} or tmp.parentid2=#{bookbranchid} or tmp.parentid3=#{bookbranchid} )
and (m.bookstaffid=#{bookstaffid}
or exists( select 1 from attendee att where att.meetingid=m.meetingid and att.staffid in
#{listItem}
) )
AND EXISTS ( SELECT 1 FROM location l1 LEFT JOIN location l2 ON (l1.locationid = l2.parentid AND l2.status = '1') LEFT JOIN location l3 ON (l3.parentid = l2.locationid AND l3.status = '1') LEFT JOIN location l4 ON (l4.parentid = l3.locationid AND l4.status = '1') WHERE l1.status = '1' AND l1.locationid =#{locationid} AND (l1.locationid = mr.locationid OR l2.locationid = mr.locationid OR l3.locationid = mr.locationid OR l4.locationid = mr.locationid ) )
order by m.starttime asc
order by m.starttime desc

SELECT m.*, st.name as bookstaffname, br.name as bookbranchname, mr.name AS meetingroomname, l.name AS locationname FROM meeting m LEFT JOIN meetingroom mr ON (m.status = '1' AND m.meetingroomid = mr.meetingroomid) LEFT JOIN staff st ON (m.bookstaffid = st.staffid) LEFT JOIN branch br ON (m.bookbranchid = br.branchid) LEFT JOIN location l ON (mr.locationid = l.locationid) WHERE mr.orgid = #{orgid} and mr.status='1' and periodmeetingid=0
and (m.subject like #{search} )
and m.meetingroomid=#{meetingroomid}
and m.starttime>=#{starttime}
and m.endtime <= #{endtime}
and m.auditstatus in
#{listItem}
and m.bookbranchid IN ( select tmp.branchid from branch tmp where tmp.branchid=#{bookbranchid} or tmp.parentid=#{bookbranchid} or tmp.parentid2=#{bookbranchid} or tmp.parentid3=#{bookbranchid} )
and (m.bookstaffid=#{bookstaffid}
or exists( select 1 from attendee att where att.meetingid=m.meetingid and att.staffid in
#{listItem}
) )
AND EXISTS ( SELECT 1 FROM location l1 LEFT JOIN location l2 ON (l1.locationid = l2.parentid AND l2.status = '1') LEFT JOIN location l3 ON (l3.parentid = l2.locationid AND l3.status = '1') LEFT JOIN location l4 ON (l4.parentid = l3.locationid AND l4.status = '1') WHERE l1.status = '1' AND l1.locationid =#{locationid} AND (l1.locationid = mr.locationid OR l2.locationid = mr.locationid OR l3.locationid = mr.locationid OR l4.locationid = mr.locationid ) )
order by m.starttime asc
order by m.starttime desc

	  select  
	      m.*
	  from meeting m
      where   m.status='1' 
             and  m.orgid=#{orgid} and m.meetingroomid=#{meetingroomid} 

         and m.meetingid!=#{meetingid}

      and ( m.periodflag='0' or m.periodmeetingid>0 )
      and m.starttime < #{endtime}  and  m.endtime > #{starttime}

select date_format(starttime,'%Y-%m-%d') as date,count(1) as meetings_total from meeting m where m.orgid=#{orgid} and m.status='1' and m.auditstatus in ('1','9') and (m.periodflag='0' or m.periodmeetingid>0)
and m.starttime>=#{startdate}
and m.endtime<#{enddate}
group by date order by date asc

    select count(1)  
    from meeting m
    where  m.orgid=#{orgid} and m.status='1'and m.auditstatus in ('1','9') 
         and (m.periodflag='0' or m.periodmeetingid>0)

	      and  m.starttime>=#{starttime}


	      and m.endtime< #{endtime}

select date_format(starttime,'%Y-%m-%d') as date,count(DISTINCT meetingroomid) as used_total from meeting m where m.orgid=#{orgid} and m.status='1' and m.auditstatus in ('1','9') and (m.periodflag='0' or m.periodmeetingid>0)
and m.starttime>=#{startdate}
and m.endtime<#{enddate}
group by date order by date asc
