mapper.select:
select * from mmedia
where objtype = #{objtype}
and objid = #{objid}
and xcount = #{xcount}
and ycount = #{ycount}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into mmedia (objtype, objid, xcount, 
  ycount, status, createtime
  )
values (#{objtype,jdbcType=CHAR}, #{objid,jdbcType=INTEGER}, #{xcount,jdbcType=INTEGER}, 
  #{ycount,jdbcType=INTEGER}, #{status,jdbcType=CHAR}, #{createtime,jdbcType=TIMESTAMP}
  )
