mapper.select:
select * from monthlyplaylog
where deviceid=#{deviceid}
and mediatype = #{mediatype}
and mediaid = #{mediaid}
and playmonth = #{month}

select mediatype, mediaid, sum(total) as amount, count(distinct deviceid) as dcount, sum(persons) as persons, sum(age1) as age1, sum(age2) as age2, sum(age3) as age3, sum(age4) as age4, sum(age5) as age5 from monthlyplaylog where orgid = #{orgid}
and branchid = #{branchid}
group by mediatype, mediaid order by amount desc
limit 0, ${length}
