mapper.select:
select * from monthlyplaylog
where deviceid=#{deviceid}
and mediatype = #{mediatype}
and mediaid = #{mediaid}
and playmonth = #{month}

select mediatype, mediaid, sum(total) as amount, count(distinct deviceid) as dcount
from monthlyplaylog
where orgid = #{orgid}

  and branchid = #{branchid}

group by mediatype, mediaid
order by amount desc

  limit 0, ${length}
