mapper.select:
select count(1) from pflowlog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d%H') = date_format(now(),'%Y%m%d%H')

select count(1) from pflowlog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d%H') = date_format(date_sub(now(), interval 1 hour),'%Y%m%d%H')

select count(1) from pflowlog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d') = date_format(now(),'%Y%m%d')

select count(1) from pflowlog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d') = date_format(date_sub(now(), interval 1 day),'%Y%m%d')

select count(1) from device
where status = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and (terminalid like #{search} or name like #{search})

select deviceid, terminalid, name, onlineflag from device
where status = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and (terminalid like #{search} or name like #{search})

order by deviceid

  limit ${start}, ${length}

select date_format(starttime,'%d') as sequence, date_format(starttime,'%Y%m%d') as period, count(1) as amount
from pflowlog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m') = #{month}
group by period
order by period

select date_format(starttime,'%H') as sequence, date_format(starttime,'%Y%m%d%H') as period, count(1) as amount
from pflowlog
where deviceid = #{deviceid}
and date_format(starttime,'%Y%m%d') = #{day}
group by period
order by period
