mapper.select:
select count(1) from playlog
where orgid = #{orgid}

  and deviceid = #{deviceid}

select * from playlog
where orgid = #{orgid}

  and deviceid = #{deviceid}

order by playlogid desc 

  limit ${start}, ${length}

select count(1) from device
where status = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and (terminalid like #{search} or name like #{search})

select deviceid, terminalid, name, onlineflag from device
where status = '1' 
and orgid = #{orgid}

  and branchid = #{branchid}


  and (terminalid like #{search} or name like #{search})

order by deviceid

  limit ${start}, ${length}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into playlog (orgid, branchid, deviceid, 
  starttime, endtime, duration, bundleid, 
  layoutdtlid, mediatype, mediaid
  )
values (#{orgid,jdbcType=INTEGER}, #{branchid,jdbcType=INTEGER}, #{deviceid,jdbcType=INTEGER}, 
  #{starttime,jdbcType=TIMESTAMP}, #{endtime,jdbcType=TIMESTAMP}, #{duration,jdbcType=INTEGER}, #{bundleid,jdbcType=INTEGER}, 
  #{layoutdtlid,jdbcType=INTEGER}, #{mediatype,jdbcType=CHAR}, #{mediaid,jdbcType=INTEGER}
  )
