mapper.select:
select r.*
from role r, staffrole sr
where r.roleid = sr.roleid and sr.staffid = #{staffid}

select

from role
where subsystem = #{subsystem}

  and vspid = #{vspid}


  and orgid = #{orgid}

order by roleid desc

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into role (subsystem, vspid, orgid, 
  name, createtime, createstaffid
  )
values (#{subsystem,jdbcType=CHAR}, #{vspid,jdbcType=INTEGER}, #{orgid,jdbcType=INTEGER}, 
  #{name,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{createstaffid,jdbcType=INTEGER}
  )
