mapper.select:
select sc.schoolclassid, sc.name, IFNULL(sc.description,'') as description, IFNULL(sc.classroomid,'') as classroomid, IFNULL(cr.name,'') as classroomname from schoolclass sc left join classroom cr on sc.classroomid = cr.classroomid where sc.orgid = #{orgid}
and (sc.name like #{search} or sc.description like #{search} or cr.name like #{search})
order by sc.schoolclassid desc

select     

from schoolclass
where orgid=#{orgid} and classroomid=#{classroomid}

select  count(1) 
from  schoolclass 
where classroomid=#{classroomid}

   and schoolclassid!=#{excludeschoolclassid}

select count(1)  from  schoolclass 
where orgid=#{orgid}  and name=#{name}

   and  schoolclassid != #{excludeid}

select  

from schoolclass
where name=#{name} and orgid=#{orgid}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into schoolclass (orgid, classroomid, name, 
  description, createstaffid, createtime
  )
values (#{orgid,jdbcType=INTEGER}, #{classroomid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{createstaffid,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}
  )
