mapper.select:
select count(1) from student
where orgid = #{orgid}

  and schoolclassid = #{schoolclassid}


  and ( st.name like #{search} or st.studentno like #{search}  or st.hardid like #{search} or sc.name like #{search})

select st.*,IFNULL(sc.name,'') as schoolclassname   
from student st left join schoolclass sc 
on st.schoolclassid=sc.schoolclassid
where st.orgid = #{orgid}

  and st.schoolclassid = #{schoolclassid}


  and ( st.name like #{search} or st.studentno like #{search}  or st.hardid like #{search} or sc.name like #{search})

order by st.studentid desc 

  limit ${start}, ${length}

