mapper.select:
select count(1) from templet
where status = 1 
and orgid = #{orgid}

  and ratio = #{ratio}


  and touchflag = #{touchflag}


  and homeflag = #{homeflag}


  and publicflag = #{publicflag}


  and name like #{search}

select 
from templet
where status=1
and orgid = #{orgid}

  and ratio = #{ratio}


  and touchflag = #{touchflag}


  and homeflag = #{homeflag}


  and publicflag = #{publicflag}


  and name like #{search}

order by templetid desc 

  limit ${start}, ${length}

mapper.delete:
