mapper.select:
select count(1) from video 
where status = 1

  and orgid=#{orgid}


  and branchid=#{branchid}


  and folderid=#{folderid}


  and type=#{type}


  and previewflag=#{previewflag}


  and format=#{format}


  and name like #{search}

select  from video
where status=1 

  and orgid=#{orgid}


  and branchid=#{branchid}


  and folderid=#{folderid}


  and type=#{type}


  and previewflag=#{previewflag}


  and format=#{format}


  and name like #{search}

order by videoid desc 

  limit ${start}, ${length}

mapper.delete:
update video
set status='9'
where videoid = #{videoid}
