mapper.select:
select * from devicegroup
where status=1 and orgid=${orgid} and branchid=${branchid}

  and name like '%${search}%'

order by devicegroupid desc 

  limit ${start}, ${length}

mapper.insert:

  SELECT LAST_INSERT_ID() as devicegroupid

insert into devicegroup (orgid, branchid, name, code, 
  description, status, createtime, createstaffid
  )
values (#{orgid,jdbcType=INTEGER}, #{branchid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{code,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}, #{createtime,jdbcType=TIMESTAMP}, #{createstaffid,jdbcType=INTEGER}
  )
