mapper.select:
select dgd.* from device d, devicegroupdtl dgd
where d.deviceid=dgd.deviceid and dgd.devicegroupid=${devicegroupid}
order by dgd.devicegroupdtlid desc 

	limit ${start}, ${length}

select * from devicegroupdtl
where deviceid=${deviceid}
order by devicegroupdtlid desc

mapper.update:
update devicegroupdtl
set devicegroupid = #{devicegroupid,jdbcType=INTEGER},
  deviceid = #{deviceid,jdbcType=INTEGER}
where devicegroupdtlid = #{devicegroupdtlid,jdbcType=INTEGER}
