mapper.select:
select count(1) from media where status = 1

  and orgid=${orgid}


  and branchid=${branchid}


  and type=${type}


  and genre like '%${genre}%'


  and name like '%${search}%'

select  from media
where status=1 

  and orgid=${orgid}


  and branchid=${branchid}


  and type=${type}


  and genre like '%${genre}%'


  and name like '%${search}%'

order by mediaid desc 

  limit ${start}, ${length}
