mapper.select:
select * from widgetsource
where orgid=#{orgid}
order by widgetsourceid desc 

	limit ${start}, ${length}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into widgetsource (orgid, name, uri, 
  description, createtime, createstaffid
  )
values (#{orgid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{uri,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{createstaffid,jdbcType=INTEGER}
  )
